mapper.select:
select

from nt_hik_cameras

  is_scope ='1'

    and camera_index_code = #{cameraIndexCode}



    AND d."name" LIKE #{pattern}


    and cross_id = #{crossId}


    and coll_status = #{collStatus}


        and info_source = #{infoSource}


order by modify_time desc,camera_index_code asc

mapper.update:
update nt_hik_cameras
set is_scope = '2'
where camera_index_code = #{cameraIndexCode,jdbcType=VARCHAR}
