INSERT INTO `app_accident` VALUES ('1', '31010582018000033101058201800003', '2018-01-09 17:20:42.000000', '上海', '市辖区', '1', '0', '中山西路出长宁路南约80米', '其他影响安全行为', '2018年01月09日17时20分，李广新(男，50岁)，驾沪AF65706小型普通客车，驾驶证号(230302196707055013)，沿中山西路行驶至中山西路出长宁路南约80米，与陈广鑫(男，87岁)其它非机动车骑发生因相撞致李广新无伤害，陈广鑫死亡，直接财产损失1000元，两车损坏的交通事故。', '碰撞运动车辆', '侧面碰撞(直角)', '同等', '无伤害', '沪AF65706', '比亚迪牌', 'LGXCD4C34H0119795', '小型普通客车', '0', 'BYD6460STHEV5', '直型');
