INSERT INTO `app_accident` VALUES ('3', '3201078201800002', '2018-02-02 17:20:00.000000', '江苏', '南京市', '1', '0', '幕府西路方家营路口', '其他意外', '    2018年02月02日17时20分左右，王明亚驾驶苏A18815D号大型普通客车沿幕府西路由东向西行驶至方家营路路口左转弯过程中，其所驾车前部右侧与同向左转弯张红莲驾驶的“南京U26056”电动自行车（未悬挂号牌）相碰，造成张红莲倒地被碾压当场死亡的道路交通事故。', '碰撞运动车辆', '其他角度碰撞', '无责', '无伤害', '苏A18815D', '比亚迪牌', 'LC06S24R1F1991839', '大型普通客车', '0', 'CK6121LGEV', '掉头');
