INSERT INTO `app_accident` VALUES ('5', '4403035201800007', '2018-03-02 22:02:00.000000', '广东', '深圳市', '1', '3', '南湖街道办文锦路与春风路交汇处路口', '违反交通信号(非)', '  2018年3月2日22时02分许，郑波驾驶粤BD03472号牌出租车在文锦南路南往北方向行驶，行至春风路口，车头与西往东方向过路口，由尹浩云驾驶的电动自行车发生碰撞，粤BD03472号牌出租车冲上人行安全岛，车头再与安全岛边上由廖运福驾驶的电动自行车、在安全岛上的行人陈为濠、邓清辉驾驶的共享自行车发生碰撞，造成车辆部分损坏，廖运福送医院抢救无效死亡、邓清辉、陈为濠受伤的道路交通事故。', '碰撞运动车辆', '同向刮擦', '次要', '无伤害', '粤BD03472', '比亚迪牌', 'LC0CE4DB*********', '小型轿车', '1', 'BYD7006BEVH', '停车');
